import React, {Component} from 'react';
import axios from "axios";
import Nav from './nav'

class PlayerStats extends Component {
  constructor(props){
    super(props)
    this.state={
      playerName: null,
      playerStats: {}
    }
  }

handleSubmit = (e) => {
  e.preventDefault();
  this.getPlayerId()
  console.log(this.state.playerName)
}

handleChange = (event) => {
  const replace = event.target.value.split(" ").join("_");
  if(replace.length > 0){
    this.setState({playerName: replace})
  } else {
    alert("Please type players name!")
  }
}

  getPlayerId = () => {
    axios.get(`https://www.balldontlie.io/api/v1/players?search=${this.state.playerName}`)
    .then(async res => {

      if(res.data.data[0] === undefined){
        alert("This player hasn't played yet!")
      } else if(res.data.data.length > 1){
        alert("Pleases specify the name more!")
      } else{
        await this.getPlayerStats(res.data.data[0].id)

      }
    }).catch(err => {
      console.log(err)
    })
  }

  getPlayerStats = (playerId) => {
    axios.get(`https://www.balldontlie.io/api/v1/season_averages?&player_ids[]=${playerId}`)
    .then(async res => {
      console.log(res.data.data)
      this.setState({ playerStats: res.data.data[0]})
    }).catch(err => {
      console.log(err)
    })
  }
  
  render(){
  return (
    
    <div className="App">

              <h3>Enter a players name to see their stats this season!</h3>
     <form onSubmit={this.handleSubmit}>
       <label>
         Player Name
    
         <input 
          type="text"
          value={this.state.value}
          onChange={this.handleChange}
          placeholder="please enter players name"
         />
       </label>
       <input type="submit" value="Submit"/>
     </form>
     <ul>
     <br/>
     games played: {this.state.playerStats["games_played"]}
     <br />
     points averaged: {this.state.playerStats["pts"]}
     <br />
     assists averaged: {this.state.playerStats["ast"]}
     <br />
     rebounds averaged: {this.state.playerStats["reb"]}
     </ul>

     <ul>
  
     Field Goal %: {this.state.playerStats["fg_pct"]}
 

     </ul>

    </div>
  );
}
}
export default PlayerStats;
